import React, { useState } from 'react';
import ReCAPTCHA from 'react-google-recaptcha';

const ContactForm = () => {
  const [formData, setFormData] = useState({ to: '', subject: '', message: '' });
  const [status, setStatus] = useState('');
  const [captchaValue, setCaptchaValue] = useState<string | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCaptchaChange = (value: string | null) => {
    setCaptchaValue(value);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!captchaValue) {
      setStatus('Please complete the CAPTCHA.');
      return;
    }

    try {
      const res = await fetch('http://localhost:5000/api/email', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await res.json();

      if (res.ok) {
        setStatus('Email sent!');
      } else {
        setStatus(data.error || 'Something went wrong');
      }
    } catch (err) {
      setStatus('Server error');
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 px-4">
      <h1 className="text-3xl font-bold mb-2 text-center">Contact Us</h1>
      <p className="text-gray-600 mb-6 text-center">
        We'd love to hear from you. Fill in the form below to send us a message.
      </p>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="to"
          type="email"
          placeholder="Recipient Email"
          onChange={handleChange}
          required
          className="w-full p-3 border border-gray-300 rounded"
        />
        <input
          name="subject"
          type="text"
          placeholder="Subject"
          onChange={handleChange}
          required
          className="w-full p-3 border border-gray-300 rounded"
        />
        <textarea
          name="message"
          placeholder="Message"
          rows={5}
          onChange={handleChange}
          required
          className="w-full p-3 border border-gray-300 rounded"
        />

        <ReCAPTCHA
          sitekey="YOUR_RECAPTCHA_SITE_KEY"
          onChange={handleCaptchaChange}
        />

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Send
        </button>
        {status && <p className="text-sm text-center text-gray-700">{status}</p>}
      </form>
    </div>
  );
};

export default ContactForm;
