import React, { useState } from 'react';

const ContactForm = () => {
  const [formData, setFormData] = useState({ to: '', subject: '', message: '' });
  const [status, setStatus] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/email', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await res.json();

      if (res.ok) {
        setStatus('Email sent!');
      } else {
        setStatus(data.error || 'Something went wrong');
      }
    } catch (err) {
      setStatus('Server error');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 max-w-xl mx-auto mt-10">
      <input name="to" type="email" placeholder="Recipient Email" onChange={handleChange} required className="w-full p-2 border" />
      <input name="subject" type="text" placeholder="Subject" onChange={handleChange} required className="w-full p-2 border" />
      <textarea name="message" placeholder="Message" onChange={handleChange} required className="w-full p-2 border" />
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Send</button>
      {status && <p>{status}</p>}
    </form>
  );
};

export default ContactForm;
